mapper.update:
update board 
set b_no=#{b_no} ,b_title=#{b_title}, b_content=#{b_content}, u_id=#{u_id}, b_regdate=curdate(), b_img=#{b_img}
where b_no=#{b_no}

update board
set b_del = 1
where b_no=#{b_no}

update b_reply
set u_id=#{u_id} ,re_content = #{re_content}, re_date = curdate()
where (re_no = #{re_no});
